import React, { useState } from 'react'
import { useParams } from 'react-router-dom'

const ProductDetail = () => {
    const { id } = useParams();
    const [products, setProduct] = useState(null)

    useEffect(() => {
        // Fetch the product details based on the ID (you need to implement this)
        // For example, you can fetch it from your products state in the CreateProduct component
        // Replace this with your actual data fetching logic
        const fetchProductDetails = () => {
            // Assume products is an array of products in your CreateProduct component
            const product = products.find((p) => p.id === id);
            setProduct(product);
        };

        fetchProductDetails();
    }, [id]);

    console.log(products);
    return (
        <div>
            <h1>Your {id}</h1>
        </div>
    )
}

export default ProductDetail
