import React, { useState } from 'react';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleLogin = (event) => {
        event.preventDefault();

        const dummyUser = { username: 'admin', password: 'password123' };
        const storedUser = JSON.parse(localStorage.getItem('user'));

        if (storedUser && storedUser.username === username && storedUser.password === password) {
            // User exists in local storage, set isLoggedIn to true
            localStorage.setItem('isLoggedIn', true);
            window.location.reload();
        } else if (username === dummyUser.username && password === dummyUser.password) {
            // Dummy user is used for initial login, set user in local storage and isLoggedIn to true
            localStorage.setItem('user', JSON.stringify(dummyUser));
            localStorage.setItem('isLoggedIn', true);
            window.location.reload();
        } else {
            // Invalid username or password
            setErrorMessage('Invalid username or password');
        }
    };

    const handleLogout = () => {
        // Logout: clear user data and set isLoggedIn to false
        localStorage.removeItem('user');
        localStorage.removeItem('isLoggedIn');
        window.location.reload();
    };

    // Check if the user is logged in
    const isLoggedIn = localStorage.getItem('isLoggedIn');

    return (
        <div>
            {isLoggedIn ? (
                <div>
                    <p>Welcome, {username || 'Guest'}!</p>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <form onSubmit={handleLogin}>
                    <label>
                        Username:
                        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                    </label>
                    <br />
                    <label>
                        Password:
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                    </label>
                    <br />
                    <button type="submit">Login</button>
                    {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                </form>
            )}
        </div>
    );
};

export default Login;
