import React, { useState } from 'react';
import './Login.css'; // Create a separate CSS file for component-specific styling

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleLogin = (event) => {
        event.preventDefault();

        const dummyUser = { username: 'admin', password: 'password123' };
        const storedUser = JSON.parse(localStorage.getItem('user'));

        if (storedUser && storedUser.username === username && storedUser.password === password) {
            // User exists in local storage, set isLoggedIn to true
            localStorage.setItem('isLoggedIn', true);
            window.location.reload();
        } else if (username === dummyUser.username && password === dummyUser.password) {
            // Dummy user is used for initial login, set user in local storage and isLoggedIn to true
            localStorage.setItem('user', JSON.stringify(dummyUser));
            localStorage.setItem('isLoggedIn', true);
            window.location.reload();
        } else {
            // Invalid username or password
            setErrorMessage('Invalid username or password');
        }
    };

    const handleLogout = () => {
        // Logout: clear user data and set isLoggedIn to false
        localStorage.removeItem('user');
        localStorage.removeItem('isLoggedIn');
        window.location.reload();
    };

    // Check if the user is logged in
    const isLoggedIn = localStorage.getItem('isLoggedIn');

    return (
        <div className="container mt-5">
            {isLoggedIn ? (
                <div>
                    <p className="fs-4">Welcome, {username || 'Guest'}!</p>
                    <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <form onSubmit={handleLogin} className="mt-3">
                    <div className="mb-3">
                        <label htmlFor="username" className="form-label">Username:</label>
                        <input type="text" className="form-control" id="username" value={username} onChange={(e) => setUsername(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password:</label>
                        <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                    </div>
                    <button type="submit" className="btn btn-primary">Login</button>
                    {errorMessage && <p className="text-danger mt-2">{errorMessage}</p>}
                </form>
            )}
        </div>
    );
};

export default Login;
